export class Point {
  constructor(_x, _y) {
    this._x = _x;
    this._y = _y;
  }
  static from(x, y) {
    return new Point(x, y);
  }
  get x() {
    return this._x;
  }
  get y() {
    return this._y;
  }
  toString() {
    return `x: ${this._x}, y: ${this._y}`;
  }
  add(x, y) {
    return Point.from(this._x + x, this._y + y);
  }
  static distance(p1, p2) {
    return Math.sqrt(Math.pow(p1.x - p2.x, 2) + Math.pow(p1.y - p2.y, 2));
  }
  static slope(p1, p2) {
    return (p2.y - p1.y) / (p2.x - p1.x);
  }
}
